import * as React from 'react';
import styled from 'styled-components';
import { device } from '../../../helpers';

const defaults = {
  gutter: '30px',
  col: 1,
  colTablet: 2,
  colLaptop: 3,
  colDesktop: 4
};

const GridWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  background: red;
  // ${props => props.col && "border: 3px solid blue;"};

  > div {
    margin-left: ${props => props.gutter ? props.gutter : defaults.gutter};
    width: ${props => props.col ? (props.col / 12 * 100) - props.gutter: defaults.col}%;

    // @media ${device.tablet} {
    //   width: ${props => props.colTablet ? (props.colTablet / 12 * 100) - props.gutter : defaults.colTablet}%;
    // }

    // @media ${device.laptop} {
    //   width: ${props => props.colLaptop ? (props.colLaptop / 12 * 100) - props.gutter : defaults.colLaptop}%;
    // }

    // @media ${device.desktop} {
    //   width: ${props => props.colDesktop ? (props.colDesktop / 12 * 100) - props.gutter : defaults.colDesktop}%;
    // }
  }
`;

const Grid = (props) => {
  return (
    <GridWrapper {...props}>
      {props.children}
    </GridWrapper>
  )
}

export default Grid
