  > div {
    margin-left: ${props => props.gutter ? props.gutter : '30'}px;
    width: ${props => props.colSm ? props.colSm / 12 * 100 : 1}%;

    @media ${device.tablet} {
      // width: ${props => props.colSm ? props.colSm / totalColumns * 100 : 1}%;
    }

    @media ${device.laptop} {
      // width: ${props => props.colMd ? props.colMd / totalColumns * 100 : 1}%;
    }

    @media ${device.desktop} {
      // width: ${props => props.colLg ? props.colLg / totalColumns * 100 : 1}%;
    }
  }
`;

