import * as React from 'react';
import styled from 'styled-components';
import { device } from '../../../helpers';

const defaults = {
  gutter: 30,
  col: 1,
  colTablet: 2,
  colLaptop: 3,
  colDesktop: 4
};

const GridWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  background: red;

  > div {
    margin-left: ${props => props.gutter ? props.gutter : defaults.gutter}px;
    width: ${props => props.col ? props.col / 12 * 100 : props.col}%;

    @media ${device.tablet} {
      // width: ${props => props.colTablet ? props.colTablet / 12 * 100 : defaults.colTablet}%;
    }

    @media ${device.laptop} {
      // width: ${props => props.colLaptop ? props.colLaptop / 12 * 100 : defaults.colLaptop}%;
    }

    @media ${device.desktop} {
      // width: ${props => props.colDesktop ? props.colDesktop / 12 * 100 : defaults.colDesktop}%;
    }
  }
`;

const Grid = (props) => {
  console.log(`Gutter: ${props.gutter}`);
  console.log(`Col: ${props.col}`);

  return (
    <GridWrapper>
      {props.children}
    </GridWrapper>
  )
}

export default Grid
