import styled from 'styled-components';

export const GridWrapper = styled.div`
  display: flex;
  background: red;

  > div {
    margin-left: ${props => props.gutter ? props.gutter : '20'};
  }
`;

const Grid = (props) => {
  // const gutter = props.gutter ? props.gutter : '20';

  // console.log(gutter);

  return (
    <GridWrapper>
      {props.children}
    </GridWrapper>
  )
}

export default Grid
