import * as React from 'react';
import styled from 'styled-components';
import { device } from '../../../helpers';

const settings = {
  //defaults
  gutter: '30px',
  col: 1,
  colTablet: 2,
  colLaptop: 3,
  colDesktop: 4
};

const updateSettings = props => {
  if (props.gutter) settings.gutter = props.gutter;
  if (props.col) settings.col = props.col;
  if (props.colTablet) settings.colTablet = props.colTablet;
  if (props.colLaptop) settings.colLaptop = props.colLaptop;
  if (props.colDesktop) settings.colDesktop = props.colDesktop;
};

const GridWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  background: red;
  // ${props => props.col && "border: 3px solid blue;"};

  > div {
    margin-left: ${props => props.gutter};
    width: calc(${settings.col / 12 * 100} - ${settings.gutter};

    // @media ${device.tablet} {
    //   width: ${props => props.colTablet ? (props.colTablet / 12 * 100) - props.gutter : settings.colTablet}%;
    // }

    // @media ${device.laptop} {
    //   width: ${props => props.colLaptop ? (props.colLaptop / 12 * 100) - props.gutter : settings.colLaptop}%;
    // }

    // @media ${device.desktop} {
    //   width: ${props => props.colDesktop ? (props.colDesktop / 12 * 100) - props.gutter : settings.colDesktop}%;
    // }
  }
`;

const Grid = (props) => {

  updateSettings(props);

  console.log(settings.gutter);

  return (
    <GridWrapper {...props}>
      {props.children}
    </GridWrapper>
  )
}

export default Grid
