  gutter: '30px',
  col: 1,
  colTablet: 2,
  colLaptop: 3,
  colDesktop: 4
};

const GridWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  background: red;
  // ${props => props.col && "border: 3px solid blue;"};

  > div {
    margin-left: ${props => props.gutter ? props.gutter : settings.gutter};
    width: ${props => props.col ? (props.col / 12 * 100) - props.gutter: settings.col}%;

    // @media ${device.tablet} {
    //   width: ${props => props.colTablet ? (props.colTablet / 12 * 100) - props.gutter : settings.colTablet}%;
    // }

